mapper.select:
select * from bsp_orders where uid=${UID} 
and userrole = 1 and ncstate = 0 
order by oid desc
limit ${START_NUM},${LIMIT_NUM}

SELECT
o.*
FROM
bsp_orders o,
bsp_users u
WHERE
o.uid = u.uid

	and o.osn =#{orderNum}


	and u.username =#{username}


	and o.mobile =#{mobile}


	and o.consignee =#{consignee}


	and o.orderstate =#{orderstate}


	and o.userrole =#{roleid}

order by o.oid desc
limit ${startNum},${limitNum}

SELECT
count(1)
FROM
bsp_orders o,
bsp_users u
WHERE
o.uid = u.uid

	and o.osn =#{orderNum}


	and u.username =#{username}


	and o.mobile =#{mobile}


	and o.consignee =#{consignee}


	and o.orderstate =#{orderstate}


	and o.userrole =#{roleid}

SELECT
price
FROM
bsp_productsregions pr,
bsp_productsregionsprice prp
WHERE
pr.Id = prp.productregionsId
AND pr.productId = #{productId}
AND pr.regionsId
= #{regionsId}
AND prp.priceType = #{priceType}
AND prp.type = #{type}
limit 1
